import React from 'react';

class Clock extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			time: new Date().toLocaleString()
		};
	}
	render(){
		return (<div className="tc">
					<h1>The time is {this.state.time}.</h1>
				</div>)
	}
}

export default Clock;
